import React, { useState } from "react";
import Axios from "axios";


function App() {

  const [data, setData] = useState({
    code: 0,
    token: ""
  });
  const [apiData, setApiData] = useState({
    link: "https://api.ecoledirecte.com/v3/login.awp?v=1.8.26",
    params: {"identifiant": "diegof", "motdepasse": "6xioapk9"}
  });

  const getToken = async() => {
    return Axios.get(apiData.link, { params: {
      data: apiData.params
    }})
  }

  const getData = () => {
    getToken().then((response) => {
      if (response.data.code === 200) {
        const studentID = response.data.data.accounts[0].id;
        const studentToken = response.data.token;

        const url = 'https://api.ecoledirecte.com/v3/eleves/' + studentID + '/notes.awp?verbe=get&v=1.8.26';
        const payload = {
          "x-token": studentToken
        };

        setApiData({
          link: url,
          params: payload
        });

        getToken();

      } else {
        console.log("Failed to connect ...");
      }
    })
  }

  return (
    <div className="App">
      <h1>Moyennes Ã‰cole Directe</h1>
      <button onClick={getData}>Get Data</button>
      <h1>Code : {data.code}</h1>
    </div>
  );
}

export default App;
