import React, { useState } from "react";
import './App.css';


function App() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const axios = require('axios');

  const [isValidated, setIsValidated] = useState(false);
  const [loginSuccessful, setLoginSuccessful] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const [isThereGrades, setIsThereGrades] = useState(false);
  const [grades, setGrades] = useState({});

  const handleUsernameChange = event => {
    setUsername(event.target.value);
  }

  const handlePasswordChange = event => {
    setPassword(event.target.value);
  }

  const getData = async() => {
    setIsLoading(true);

    const loginURL = "https://api.ecoledirecte.com/v3/login.awp?v=4.17.9";

    const loginPayload = {
      "uuid": "",
      "identifiant": username,
      "motdepasse": password,
      "isReLogin": false
    }

    axios.get(loginURL, { params: { data: loginPayload } }).then((loginResponse) => {
      const loginResponseCode = loginResponse.data.code;
      if (loginResponseCode !== 200) {
        setIsValidated(false);
        setLoginSuccessful(false);
        setIsLoading(false);
        return 0;
      }
      setLoginSuccessful(true);

      const studentToken = loginResponse.data.token;
      const studentID = loginResponse.data.data.accounts[0].id;

      const gradesURL = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9";

      const gradesPayload = {
        "anneeScolaire": ""
      }

      const gradesHeaders = {
        "x-token": studentToken
      }

      axios.get(gradesURL, { params: { data: gradesPayload }, headers: gradesHeaders }).then((gradesResponse) => {
        const gradesResponseCode = gradesResponse.data.code;
        if (gradesResponseCode !== 200) {
          setIsLoading(false);
          return 0;
        }
        calculateAverages(gradesResponse.data);
      });
    })
  }

  const calculateAverages = ({ data }) => {
    if (data.notes.length > 0) { setIsThereGrades(true); }

    /*
    CALCULER LES MOYENNES ICI
    */

    console.log(data);

    setIsLoading(false);
  }

  const submit = async() => {
    if (!isLoading) {
      setIsValidated(true);
      getData();
    }
  }

  return (
    <div className="App">
      {(() => {
        return (
            <div>
              <div className="box">
                <h1>Moyennes École Directe</h1>
                <p>Entrez votre identifiant et mot de passe ED</p>
                <a>{loginSuccessful ? "" : "Identifiant ou mot de passe incorrect"}</a>
                <input onChange={handleUsernameChange} type="text" placeholder="Identifiant" required></input>
                <input onChange={handlePasswordChange} type="password" placeholder="Mot de passe" required></input>
                <button onClick={submit}>{isLoading ? "Chargement..." : "Valider"}</button>
              </div>

              {(() => {
                // Affichage des Moyennes
                if (isValidated && loginSuccessful && !isLoading) {
                  if (isThereGrades) {
                    return (
                      <div className="box">
                        <h1>Moyenne : </h1>
                      </div>
                    )
                  } else {
                    return (
                      <div className="box">
                        <h1>Pas de notes pour l'instant...</h1>
                      </div>
                    )
                  }
                }
              })()}

              <form action="https://www.ecoledirecte.com" className="box">
                <h1>École Directe</h1>
                <p>École Directe empêche de voir les moyennes, ce site les calcule.</p>
                <button type="submit">Site officiel ED</button>
                <p>DF</p>
              </form>
            </div>
          )
      })()}
    </div>

  );
}

export default App;
