import React, { useState } from "react";


function App() {
  const [username, setUsername] = useState("diegof");
  const [password, setPassword] = useState("6xioapk9");

  const axios = require('axios');

  const loginURL = "https://api.ecoledirecte.com/v3/login.awp?v=4.17.9";

  const getData = () => {
    const payload = {
      "uuid": "",
      "identifiant": username,
      "motdepasse": password,
      "isReLogin": false
    }

    axios.get(loginURL, { params: { data: payload } }).then((loginResponse) => {
      const studentToken = loginResponse.data.token;
      const studentID = loginResponse.data.data.accounts[0].id;

      const gradesURL = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9";

      const gradesPayload = {
        "anneeScolaire": ""
      }

      axios.get(gradesURL, { params: { data: gradesPayload }, headers: { "x-token": studentToken } }).then((gradesResponse) => {
        console.log(gradesResponse.data);
      })
    })
  }

  return (
    <div className="App">
      <h1>Moyennes Ã‰cole Directe</h1>
      <button onClick={getData}>Get Data</button>
    </div>
  );
}

export default App;
