import React, { useState } from "react";
import Axios from "axios";


function App() {
  const [username, setUserName] = useState("diegof");
  const [password, setPassword] = useState("6xioapk9");

  const connect = async() => {
    return Axios.get("https://api.ecoledirecte.com/v3/login.awp?v=4.17.9", { params: {
      data: {
        "identifiant": username,
        "motdepasse": password,
        "uuid": "",
        "isReLogin": false
      }
    }});
  }

  const getGrades = async({ studentToken, studentID }) => {
    return Axios.get("https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9", { headers: {
      "x-token": studentToken
    }})
  }

  const getData = () => {
    connect().then((connectionResponse) => {
      const studentToken = connectionResponse.data.token;
      const studentID = connectionResponse.data.data.accounts[0].id;

      getGrades({ studentToken, studentID }).then((gradesResponse) => {
        console.log(gradesResponse.data);
      })
    })
  }

  return (
    <div className="App">
      <h1>Moyennes Ã‰cole Directe</h1>
      <button onClick={getData}>Get Data</button>
    </div>
  );
}

export default App;
