import React, { useState } from "react";
import './App.css';


function App() {
  const [username, setUsername] = useState("diegof");
  const [password, setPassword] = useState("6xioapk9");

  const axios = require('axios');
  const [isLoading, setIsLoading] = useState(false);

  const getData = () => {
    setIsLoading(true);

    const loginURL = "https://api.ecoledirecte.com/v3/login.awp?v=4.17.9";

    const loginPayload = {
      "uuid": "",
      "identifiant": username,
      "motdepasse": password,
      "isReLogin": false
    }

    axios.get(loginURL, { params: { data: loginPayload } }).then((loginResponse) => {
      const loginResponseCode = loginResponse.data.code;
      if (loginResponseCode !== 200) {
        setIsLoading(false);
        return 0;
      }

      const studentToken = loginResponse.data.token;
      const studentID = loginResponse.data.data.accounts[0].id;

      const gradesURL = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9";

      const gradesPayload = {
        "anneeScolaire": ""
      }

      const gradesHeaders = {
        "x-token": studentToken
      }

      axios.get(gradesURL, { params: { data: gradesPayload }, headers: gradesHeaders }).then((gradesResponse) => {
        const gradesResponseCode = gradesResponse.data.code;
        if (gradesResponseCode !== 200) {
          setIsLoading(false);
          return 0;
        }

        console.log(gradesResponse.data);

        setIsLoading(false);
      })
    })
  }

  return (
    <div className="App">
      <h1>Voir ses moyennes</h1>
      <p align="center">Utiliser les codes ED :</p>

      <div className="inputs">
          <input type="text" className="username" id="username" placeholder="Identifiant" required />

          <p align="center">
              <input type="password" className="password" id="password" placeholder="Mot de passe" required />
              <i className="bi bi-eye-slash" id="togglePassword"></i>
          </p>
      </div>
      <div align="center">
          <button onClick={getData} style={{
            verticalAlign: "middle"
          }}><span>Valider</span></button>
      </div>
    </div>

  );
}

export default App;
