import React, { useState } from "react";
import Axios from "axios";


function App() {
  var [apiData, setApiData] = useState({
    link: "https://api.ecoledirecte.com/v3/login.awp?v=4.17.8",
    params: {"identifiant": "diegof", "motdepasse": "6xioapk9", "uuid": "", "isReLogin": false}
  });

  const getToken = async() => {
    return Axios.get(apiData.link, {
      data: apiData.params
    })
  }

  const getData = async() => {
    getToken().then((response) => {
      const givenData = response.data;

      if (givenData.code === 200) {
        const studentID = givenData.data.accounts[0].id;
        const studentToken = givenData.token;

        apiData.link = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.8";
        apiData.params = {
          "x-token": studentToken
        };

        Axios.post(apiData.link, {
          "x-token": studentToken
        });


      } else {
        console.log("Connection failed ...");
      }
    })
  }

  return (
    <div className="App">
      <h1>Moyennes Ã‰cole Directe</h1>
      <button onClick={getData}>Get Data</button>
    </div>
  );
}

export default App;
