import React, { useState } from "react";
import Axios from "axios";


function App() {
  const [username, setUserName] = useState("diegof");
  const [password, setPassword] = useState("6xioapk9");

  const axios = require('axios');

  const connect = async() => {
    const data = {
      "identifiant": username,
      "motdepasse": password,
      "uuid": "",
      "isReLogin": false
    };

    return axios.get("https://api.ecoledirecte.com/v3/login.awp?v=4.17.9", { params: {
      data: {
        "identifiant": username,
        "motdepasse": password,
        "uuid": "",
        "isReLogin": false
      }
    }});
  }

  const getData = async() => {
    connect().then((connectionResponse) => {
      const studentToken = connectionResponse.data.token;
      const studentID = connectionResponse.data.data.accounts[0].id;
      const url = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9";

      const data = {
        "x-token": studentToken
      };
      const headers = {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
          "x-token": studentToken
        }
      };

      axios.post(url, data, headers)
      .then((response) => {
          console.log(response.data);
      })
    })

  }

  return (
    <div className="App">
      <h1>Moyennes Ã‰cole Directe</h1>
      <button onClick={getData}>Get Data</button>
    </div>
  );
}

export default App;
