import React, { useState } from "react";
import './App.css';


function App() {
  const [username, setUsername] = useState("diegof");
  const [password, setPassword] = useState("6xioapk9");

  const axios = require('axios');
  const [isLoading, setIsLoading] = useState(false);
  const [validated, setValidated] = useState(false);
  const [isThereGrades, setIsThereGrades] = useState(false);

  var [grades, setGrades] = useState({});

  const handleUsernameChange = event => {

  }

  const handlePasswordChange = event => {

  }

  const getData = async() => {
    setIsLoading(true);

    const loginURL = "https://api.ecoledirecte.com/v3/login.awp?v=4.17.9";

    const loginPayload = {
      "uuid": "",
      "identifiant": username,
      "motdepasse": password,
      "isReLogin": false
    }

    axios.get(loginURL, { params: { data: loginPayload } }).then((loginResponse) => {
      const loginResponseCode = loginResponse.data.code;
      if (loginResponseCode !== 200) {
        setIsLoading(false);
        return 0;
      }

      const studentToken = loginResponse.data.token;
      const studentID = loginResponse.data.data.accounts[0].id;

      const gradesURL = "https://api.ecoledirecte.com/v3/eleves/" + studentID + "/notes.awp?verbe=get&v=4.17.9";

      const gradesPayload = {
        "anneeScolaire": ""
      }

      const gradesHeaders = {
        "x-token": studentToken
      }

      axios.get(gradesURL, { params: { data: gradesPayload }, headers: gradesHeaders }).then((gradesResponse) => {
        const gradesResponseCode = gradesResponse.data.code;
        if (gradesResponseCode !== 200) {
          setIsLoading(false);
          return 0;
        }
        calculateAverages(gradesResponse.data);
      });
    })
  }

  const calculateAverages = ({ data }) => {
    if (data.notes.length > 0) { setIsThereGrades(true); }

    /*
    CALCULER LES MOYENNES ICI
    */

    setIsLoading(false);
  }

  const submit = async() => {
    setValidated(true);
    getData();
  }

  return (
    <div className="App">
      {(() => {
        // Page d'accueil
        if (!validated) {
          return (
            <div>
              <h1>Moyennes École Directe</h1>
              <p>Entrez vos identifiants et mot de passe ED</p>
              <div className="inputs">
                <input id="username" onChange={handleUsernameChange} type="text" placeholder="Identifiant" required></input>
                <input id="password" onChange={handlePasswordChange} type="password" placeholder="Mot de passe" required></input>
              </div>
              <button onClick={submit}>Valider</button>
            </div>
          )

        // Page de chargement
        } else if (isLoading) {
          return (
            <div>
              <h1>Loading ...</h1>
            </div>
          )

        // Page des notes
        // À FAIRE
        } else {
          if (isThereGrades) {
            return (
              <div>
                <h1>Moyennes : </h1>
              </div>
            )
          } else {
            return (
              <div>
                <h1>Pas de notes pour l'instant ...</h1>
              </div>
            )
          }
        }
      })()}
    </div>

  );
}

export default App;
